void LevelViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &okBtn)
    {
        //ok
        //When okBtn clicked change screen to Main
        //Go to Main with screen transition towards East
        application().gotoMainScreenSlideTransitionEast();
    }
}

void LevelViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &settingsBtn)
    {
        //settings
        //When settingsBtn clicked change screen to Settings
        //Go to Settings with screen transition towards West
        application().gotoSettingsScreenSlideTransitionWest();
    }
    else if (&src == &workBtn)
    {
        //work
        //When workBtn clicked change screen to Main
        //Go to Main with screen transition towards East
        application().gotoMainScreenSlideTransitionEast();
    }
    else if (&src == &diagBtn)
    {
        //diagnostics
        //When diagBtn clicked change screen to Diagnostics
        //Go to Diagnostics with no screen transition
        application().gotoDiagnosticsScreenNoTransition();
    }
    else if (&src == &homeBtn)
    {
        //home
        //When homeBtn clicked change screen to Boot
        //Go to Boot with no screen transition
        application().gotoBootScreenNoTransition();
    }
}

